<?phpnamespace App\Filters;use CodeIgniter\Exceptions\PageNotFoundException;use CodeIgniter\HTTP\RequestInterface;use CodeIgniter\Security\Exceptions\SecurityException;class RoleFilter extends BaseFilter{    public function before(RequestInterface $request, $arguments = null)    {        self::$interfaces = class_implements(self::$controller);        if (self::$interfaces and in_array(session('tipo'), self::$interfaces) == false) {            if ($request->isAJAX()) {                throw SecurityException::forDisallowedAction();            }            throw PageNotFoundException::forPageNotFound();        }    }}