<?phpnamespace App\Filters;use CodeIgniter\HTTP\RequestInterface;use CodeIgniter\HTTP\ResponseInterface;class UpdateLogFilter extends BaseFilter{    public function after(RequestInterface $request, ResponseInterface $response, $arguments = null)    {        if ($request->getMethod() === 'post' and session()->has('logado')) {            if (in_array($response->getStatusCode(), [200, 201])) {                auth()->updateLog();            }        }    }}